import React, { Component } from "react";
import "./playbutton.css";
import "bootstrap/dist/css/bootstrap.css";

class PlayButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: true,
    };
    this.handleEffects = this.handleEffects.bind(this);
    this.handleVolChange = this.handleVolChange.bind(this);
  }

  handleEffects() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    
    console.log(document.getElementsByClassName("wrapper")[0].style.height)

    const sound = document.getElementById(this.props.id);
    if (this.state.active === true) {
      sound.play();
      sound.currentTime = 0;
    } else {
      sound.pause();
    }
  }

  handleVolChange(e) {
    const sound = document.getElementById(this.props.id);
    sound.volume = e.target.value / 100;
  }

  render() {
    return (
      <div className="wrapper">
        <div
          className={this.state.active ? "playbuttonoff" : "playbuttonon"}
          onClick={this.handleEffects}
        >
          <p>{this.props.id}</p>
          <audio src={this.props.source} id={this.props.id} loop />
        </div>
        <input
          type="range"
          min="0"
          max="100"
          className="slider"
          id="vol"
          onChange={this.handleVolChange}
        />
      </div>
    );
  }
}

export default PlayButton;
