import React, { Component } from "react";
import "./soundboard.css";
import PlayButton from "./playbutton";
import Waves from "../audio/Waves.mp3";
import BigBen from "../audio/BigBen.mp3";
import "../weather.css";
import "../weather.min.css";
import "./fireflies.sass";
import "bootstrap/dist/css/bootstrap.css";
import "./mdb.css";

const effectBank = [
  { id: "Waves", src: Waves },
  { id: "BigBen", src: BigBen },
];

class Soundboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fireflynum: 100,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    console.log(e.target.value);
    this.setState({ fireflynum: e.target.value });
  }

  render() {
    let buttons = effectBank.map((effect) => {
      return <PlayButton source={effect.src} id={effect.id} key={effect.id} />;
    });

    let fireflyquan = [];

    for (let i = 0; this.state.fireflynum > i; i++) {
      fireflyquan.push(<div className="firefly"></div>);
    }

    return (
      <div className="background-anim weather animated bound">
        <div className="soundboard animated bounce infinite">{buttons}</div>
        {fireflyquan}
        <input
          type="number"
          min="0"
          max="200"
          className="firefly-input"
          onChange={this.handleChange}
          placeholder="# of Fireflies"
        />
      </div>
    );
  }
}

export default Soundboard;
