import React, { Component } from "react";
import "./playbutton.css";
import "bootstrap/dist/css/bootstrap.css";

class PlayButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: true,
    };
    this.playSound = this.playSound.bind(this);
  }

  playSound() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    const sound = document.getElementById(this.props.id);
    console.log(document.getElementById('vol').value);
    if (this.state.active === true) {
      sound.volume = document.getElementById('vol').value
      sound.play();
      sound.currentTime = 0;
    } else {
      sound.pause();
    }
  }

  render() {
    return (
      <div className="wrapper">
        <div className="playbutton" onClick={this.playSound}>
          <p>{this.props.id}</p>
          <audio src={this.props.source} id={this.props.id} loop />
        </div>
        <div className="volSlider">
          <input
            type="range"
            min="0"
            max="100"
            className="slider"
            id="vol"
          />
        </div>
      </div>
    );
  }
}

export default PlayButton;
