import React, { Component } from "react";
import "./playbutton.css";

class PlayButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inactive: true,
    };
    this.handleEffects = this.handleEffects.bind(this);
    this.handleVolChange = this.handleVolChange.bind(this);
  }

  handleEffects() {
    const currentState = this.state.inactive;
    this.setState({ inactive: !currentState });

    const sound = document.getElementById(this.props.id);
    if (this.state.inactive === true) {
      sound.play();
      sound.currentTime = 0;
      if (this.props.muteall === true) {
        sound.volume = 0;
      }
      else {sound.volume = 0.5}
    } else {
      sound.pause();
    }
  }

  handleVolChange(e) {
    const sound = document.getElementById(this.props.id);
    console.log(this.props.muteall);
    if(this.props.muteall === true) {
      sound.volume = 0;
    }
   else {
    sound.volume = e.target.value / 100;
   }
   console.log(sound.volume);
  }

  render() {
    return (
      <div className="wrapper">
        <div
          className={
            this.props.muteall
              ? "playbuttonoff scale-out-center"
              :
            (this.state.inactive)
              ? "playbuttonoff scale-out-center"
              : "playbuttonon scale-in-center"
          }
          onClick={this.handleEffects}
        >
          {this.props.id}
          <audio src={this.props.source} id={this.props.id} loop />
          {this.props.muteall ? null : this.state.inactive ? null : this.props.anim}
        </div>
        <input
          type="range"
          min="0"
          max="100"
          className={
            this.props.muteall ? "slider slide-out-top" :
            this.state.inactive
              ? "slider slide-out-top"
              : "sliderlong scale-in-center slide-in-top"
          }
          id="vol"
          onChange={this.handleVolChange}
        />
      </div>
    );
  }
}

export default PlayButton;
