import React, { Component } from "react";
import "./playbutton.css";
import "bootstrap/dist/css/bootstrap.css";

class PlayButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: true,
      volume: 75
    };
    this.playSound = this.playSound.bind(this);
    this.handleVolChange = this.handleVolChange.bind(this);
  }

  playSound() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    const sound = document.getElementById(this.props.id);
    //console.log(document.getElementById("vol").value);
    //console.log(document.getElementById(this.props.id));
    //console.log(this.state.volume);
    if (this.state.active === true) {
      //sound.volume = document.getElementById("vol").value / 100;
      sound.play();
      sound.currentTime = 0;
    } else {
      sound.pause();
    }
  }

  handleVolChange(e) {
    const sound = document.getElementById(this.props.id);
    sound.volume
    console.log(sound)
    //this.setState({ volume: e.target.value });
  }

  render() {
    return (
      <div className="wrapper">
        <div className="playbutton" onClick={this.playSound}>
          <p>{this.props.id}</p>
          <audio src={this.props.source} id={this.props.id} loop />
        </div>
        <input
          type="range"
          min="0"
          max="100"
          className="slider"
          id="vol"
          onChange={this.handleVolChange}
        />
      </div>
    );
  }
}

export default PlayButton;
