import React, { Component } from "react";
import "./playbutton.css";
import "bootstrap/dist/css/bootstrap.css";

class PlayButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: true,
    };
    this.playSound = this.playSound.bind(this);
  }

  playSound() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    const sound = document.getElementById(this.props.id);
    console.log(this.props.source);
    if (this.state.active === true) {
      sound.play();
      sound.currentTime = 0;
    } else {
      sound.pause();
    }
  }

  render() {
    return (
      <div>
        <div className="playbutton" onClick={this.playSound} id="test2">
          <p>{this.props.id}</p>
          <audio src={this.props.source} id={this.props.id} loop />
        </div>
        <div className="">
          <form className="range-field w-20">
            <input
              id="sliderVol"
              className="border-0"
              type="range"
              min="0"
              max="100"
            />
          </form>
          <span className="font-weight-bold text-primary ml-2 mt-1 valueSpan"></span>
        </div>
      </div>
    );
  }
}

export default PlayButton;
