import React, { Component } from "react";
import "./soundboard.css";
import PlayButton from "./playbutton";
import Waves from "../audio/Waves.mp3";
import BigBen from "../audio/BigBen.mp3";
import "../weather.css";
import "../weather.min.css";
import "./fireflies.sass";

const effectBank = [
  { id: "Waves", src: Waves },
  { id: "BigBen", src: BigBen },
];

class Soundboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fireflynum: 50
    };
  }
  
  handleVolChange(e) {
    const sound = document.getElementById(this.props.id);
    sound.volume = e.target.value / 100;
  }

  render() {
    let buttons = effectBank.map((effect) => {
      return <PlayButton source={effect.src} id={effect.id} key={effect.id} />;
    });

    let fireflyquan = [];

    for (let i=0; 50>i; i++) {
      fireflyquan.push(<div className='firefly'></div>)
    }

    return (
      <div className="background-anim weather">
        <div className="soundboard">{buttons}</div>
        {fireflyquan}
        <input
          type="number"
          min="0"
          max="100"
          className="fireflyquan"
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default Soundboard;
